// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000/api';

const AQI_COLORS = {
  'Good': '#00E400',
  'Moderate': '#FFFF00',
  'Unhealthy for Sensitive Groups': '#FF7E00',
  'Unhealthy': '#FF0000',
  'Very Unhealthy': '#8F3F97',
  'Hazardous': '#7E0023'
};

function App() {
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState(null);
  const [currentAQI, setCurrentAQI] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCities();
    fetchAllCurrent();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/cities`);
      const data = await response.json();
      setCities(data.cities);
      if (data.cities.length > 0 && !selectedCity) {
        setSelectedCity(data.cities[0]);
      }
    } catch (error) {
      console.error('Error fetching cities:', error);
    }
  };

  const fetchAllCurrent = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/all-current`);
      const data = await response.json();
      // Process all cities data
    } catch (error) {
      console.error('Error fetching current AQI:', error);
    }
  };

  const fetchCityData = async (city) => {
    setLoading(true);
    try {
      // Fetch current AQI
      const currentRes = await fetch(`${API_BASE_URL}/current/${city}`);
      const currentData = await currentRes.json();
      setCurrentAQI(currentData);

      // Fetch forecast
      const forecastRes = await fetch(`${API_BASE_URL}/forecast/${city}?days=7`);
      const forecastData = await forecastRes.json();
      setForecast(forecastData);
    } catch (error) {
      console.error('Error fetching city data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (selectedCity) {
      fetchCityData(selectedCity);
    }
  }, [selectedCity]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>üåç Pakistan Air Quality Monitor</h1>
        <p>Real-time AQI & Predictions</p>
      </header>

      <div className="container">
        <div className="city-selector">
          <label>Select City: </label>
          <select 
            value={selectedCity || ''} 
            onChange={(e) => setSelectedCity(e.target.value)}
          >
            {cities.map(city => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
        </div>

        {loading && <div className="loading">Loading...</div>}

        {currentAQI && (
          <div className="current-aqi-card">
            <h2>{currentAQI.city}</h2>
            <div 
              className="aqi-display"
              style={{ 
                backgroundColor: AQI_COLORS[currentAQI.aqi_category] || '#808080',
                color: 'white'
              }}
            >
              <div className="aqi-value">{currentAQI.aqi_value}</div>
              <div className="aqi-category">{currentAQI.aqi_category}</div>
              <div className="aqi-details">
                <p>PM2.5: {currentAQI.pm25_value.toFixed(2)} ¬µg/m¬≥</p>
                <p>Last Updated: {new Date(currentAQI.datetime).toLocaleString()}</p>
              </div>
            </div>
          </div>
        )}

        {forecast && (
          <div className="forecast-section">
            <h2>7-Day Forecast</h2>
            <div className="forecast-grid">
              {forecast.data.slice(0, 24 * 7).map((item, index) => (
                <div key={index} className="forecast-item">
                  <div className="forecast-time">
                    {new Date(item.datetime).toLocaleString()}
                  </div>
                  <div 
                    className="forecast-aqi"
                    style={{ 
                      borderColor: AQI_COLORS[item.aqi_category] || '#808080'
                    }}
                  >
                    {item.aqi_predicted}
                  </div>
                  <div className="forecast-category">{item.aqi_category}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
