import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000/api';

const AQI_COLORS = {
  'Good': '#00E400',
  'Moderate': '#FFFF00',
  'Unhealthy for Sensitive Groups': '#FF7E00',
  'Unhealthy': '#FF0000',
  'Very Unhealthy': '#8F3F97',
  'Hazardous': '#7E0023'
};

function App() {
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState(null);
  const [currentAQI, setCurrentAQI] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [forecastError, setForecastError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/cities`);
      const data = await response.json();
      setCities(data.cities);
      if (data.cities.length > 0 && !selectedCity) {
        setSelectedCity(data.cities[0]);
      }
    } catch (error) {
      console.error('Error fetching cities:', error);
    }
  };

  const fetchCityData = async (city) => {
    if (!city) return;
    
    setLoading(true);
    setForecastError(null);
    setForecast(null);
    
    try {
      // Fetch current AQI
      const currentRes = await fetch(`${API_BASE_URL}/current/${city}`);
      if (!currentRes.ok) {
        throw new Error(`Failed to fetch current AQI: ${currentRes.statusText}`);
      }
      const currentData = await currentRes.json();
      setCurrentAQI(currentData);

      // Fetch forecast
      const forecastRes = await fetch(`${API_BASE_URL}/forecast/${city}?days=7`);
      if (!forecastRes.ok) {
        const errorData = await forecastRes.json();
        setForecastError(errorData.error || 'Failed to load forecast');
        console.error('Forecast error:', errorData);
      } else {
        const forecastData = await forecastRes.json();
        console.log('Forecast data received:', forecastData); // Debug log
        setForecast(forecastData);
        setForecastError(null);
      }
    } catch (error) {
      console.error('Error fetching city data:', error);
      setForecastError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (selectedCity) {
      fetchCityData(selectedCity);
    }
  }, [selectedCity]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>üåç Pakistan Air Quality Monitor</h1>
        <p>Real-time AQI & Predictions</p>
      </header>

      <div className="container">
        <div className="city-selector">
          <label>Select City: </label>
          <select 
            value={selectedCity || ''} 
            onChange={(e) => setSelectedCity(e.target.value)}
          >
            {cities.map(city => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
        </div>

        {loading && <div className="loading">Loading...</div>}

        {currentAQI && (
          <div className="current-aqi-card">
            <h2>{currentAQI.city}</h2>
            <div 
              className="aqi-display"
              style={{ 
                backgroundColor: AQI_COLORS[currentAQI.aqi_category] || '#808080',
                color: 'white'
              }}
            >
              <div className="aqi-value">{Math.round(currentAQI.aqi_value)}</div>
              <div className="aqi-category">{currentAQI.aqi_category}</div>
              <div className="aqi-details">
                <p>PM2.5: {currentAQI.pm25_value.toFixed(2)} ¬µg/m¬≥</p>
                <p>Last Updated: {new Date(currentAQI.datetime).toLocaleString()}</p>
              </div>
            </div>
          </div>
        )}

        {/* Forecast Section */}
        <div className="forecast-section">
          <h2>7-Day Air Quality Forecast</h2>
          
          {forecastError && (
            <div className="error-message">
              <p>‚ö†Ô∏è {forecastError}</p>
              <p style={{ fontSize: '0.9rem', marginTop: '0.5rem', color: '#666' }}>
                To enable forecasts, please train the models by running: <code>python train_forecast_model.py</code>
              </p>
            </div>
          )}

          {forecast && forecast.data && forecast.data.length > 0 ? (
            <>
              <p className="forecast-info">
                Showing {forecast.count} hourly predictions for the next {forecast.forecast_days} days
              </p>
              <div className="forecast-grid">
                {forecast.data
                  .filter((_, idx) => idx % 6 === 0) // Show every 6 hours (4 times per day)
                  .map((item, index) => (
                    <div 
                      key={index} 
                      className="forecast-item"
                      style={{ 
                        borderColor: AQI_COLORS[item.aqi_category] || '#808080',
                        backgroundColor: AQI_COLORS[item.aqi_category] ? 
                          `${AQI_COLORS[item.aqi_category]}20` : '#f0f0f0'
                      }}
                    >
                      <div className="forecast-time">
                        {new Date(item.datetime).toLocaleString('en-US', {
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </div>
                      <div 
                        className="forecast-aqi"
                        style={{ 
                          color: AQI_COLORS[item.aqi_category] || '#333'
                        }}
                      >
                        {Math.round(item.aqi_predicted)}
                      </div>
                      <div className="forecast-category">{item.aqi_category}</div>
                      <div className="forecast-pm25">
                        PM2.5: {item.pm25_predicted.toFixed(1)} ¬µg/m¬≥
                      </div>
                    </div>
                  ))}
              </div>
            </>
          ) : !forecastError && !loading && (
            <div className="info-message">
              <p>No forecast data available</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
